css:
.body {
  font-family: "Raleway", sans-serif;
}

.header__nav {
    font-family: raleway;
}

.products-carousel__title a, .products-carousel__title span {
    font-family: raleway;
    font-weight: 700;
}

main .product-card__name {
    font-family: 'Raleway';
}

main .product-card .prices__value {
    font-family: 'Raleway';
    font-size: 17px;
    font-weight: 600;
}

.prices:not(.prices--big) .prices__installments {
    font-weight: 700;
    font-family: 'Raleway';
    font-size: 20px;
    color: black;
}

.prices__discount{
  font-family: 'Raleway';
}

main .page-title__head h1 {
    font-family: 'Raleway';
    font-weight: 500;
    line-height: 42px;
}

.header__search-input {
    font-size: 16px;
    font-family: 'Raleway';
}

div>.header__nav .header__category {
    color: #000;
}

div>.header__nav .header__category:hover {
    color: #549358;
}

main .header__search .i-search {
    color: #549e58;
}

main .header__buttons .i-user:before {
    content: "";
    color: #549e58;
}

.fa-shopping-bag:before, .i-shopping-bag:before {
    content: "";
    color: #549e58;
}

main .header__buttons .badge-primary {
    background-color: #549e58;
}

@media (max-width: 720px) {
  .gallery__item--video {
    font-size: 25px;
    color: red;
    margin-top: -3px;
    animation: pulse 0.7s infinite;
    margin: 0 auto;
    display: table;
    margin-top: -20px;
    animation-direction: alternate;
    -webkit-animation-name: pulse;
    animation-name: pulse;
    border: solid;
    border-radius: 50px;
    padding: 5px 5px 5px 5px;
    margin-right: 15px;
  }
}
